import rclpy
from rclpy.node import Node
from geometry_msgs.msg import PoseStamped

class GoalPosePublisher(Node):
    def __init__(self):
        super().__init__('goal_pose_publisher')
        
        # Create publisher for goal pose
        try:
            self.goal_publisher = self.create_publisher(
                PoseStamped,
                'goal_pose',
                10
            )
            self.get_logger().info('Publisher created successfully')
        except Exception as e:
            self.get_logger().error(f'Failed to create publisher: {str(e)}')
            raise

        try:
            self.timer = self.create_timer(0.5, self.timer_callback)
            self.get_logger().info('Timer created successfully')
        except Exception as e:
            self.get_logger().error(f'Failed to create timer: {str(e)}')
            raise

        self.x = 0.0
        self.y = 5.0
        self.z = 0.0

    def timer_callback(self):
        try:
            self.publish_goal(self.x, self.y, self.z)
            self.get_logger().debug('Published goal pose')  # Debug level to avoid spam
        except Exception as e:
            self.get_logger().error(f'Error in timer callback: {str(e)}')
        
    def publish_goal(self, x, y, z, orientation_w=1.0):
        """Publish a goal pose for Nav2."""
        goal_pose = PoseStamped()
        
        # Set the time stamp to now
        goal_pose.header.stamp = self.get_clock().now().to_msg()
        
        # Set the coordinate frame
        goal_pose.header.frame_id = 'map'
        
        # Set the position
        goal_pose.pose.position.x = float(x)
        goal_pose.pose.position.y = float(y)
        goal_pose.pose.position.z = float(z)
        
        # Set the orientation (quaternion)
        goal_pose.pose.orientation.w = float(orientation_w)
        goal_pose.pose.orientation.x = 0.0
        goal_pose.pose.orientation.y = 0.0
        goal_pose.pose.orientation.z = 0.0
        
        # Publish the goal pose
        self.goal_publisher.publish(goal_pose)

def main(args=None):
    rclpy.init(args=args)
    
    try:
        print("Starting main function...")  # Basic print for debugging
        node = GoalPosePublisher()
        print("Node created, starting spin...")  # Basic print for debugging
        rclpy.spin(node)
    except Exception as e:
        print(f"Error in main: {str(e)}")  # Basic print for debugging
    finally:
        if 'node' in locals():
            node.destroy_node()
        rclpy.shutdown()

if __name__ == '__main__':
    main()